mapper.select:
select

from xhx_free_trlal_record

  1 = 1

    and userId in (${id})


    and trialType = #{trialType}


limit #{page},#{pageSize}

select tr.* from xhx_free_trlal_record tr inner join xhx_user u on u.id=tr.userId

  1 = 1

    and u.username like "%"#{username}"%"


    and tr.trialType = #{trialType}


limit #{page},#{pageSize}
